mapper.select:
SELECT USER_NO, USER_ID, USER_PW, USER_NAME
FROM USER_INFO
WHERE USER_ID = #{user_id}

mapper.update:
UPDATE USER_INFO
SET USER_PW=#{user_pw}
WHERE USER_ID=#{user_id}

mapper.select:
SELECT 
USER_ID=#{user_id}, USER_PW=#{user_pw}, USER_NAME=#{user_name}, USER_GENDER=#{user_gender}, USER_BIRTHDATE=#{user_birthdate},
USER_EMAIL{user_email}, USER_PHONE=#{user_phone}, USER_ZIP=#{user_zip}, USER_ADDR=#{user_addr}
FROM USER_INFO
WHERE USER_ID = #{user_id}
